import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Spinner from 'react-bootstrap/Spinner';
import './Main.css';

const API_BASE_URL = 'http://localhost:8080/api/movies'; // Update if necessary

const MoviesComponent = ({ buttonText, onBookClick }) => {
  
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get(API_BASE_URL);
        setMovies(response.data);
      } catch (error) {
        setError('Error fetching movies. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    fetchMovies();
  }, []);

  if (loading) return <Spinner animation="border" variant="primary" />;
  if (error) return <p>{error}</p>;

  return (
    <div className='main'>
      <div className='card-container'>
        {movies.length > 0 ? (
          movies.map((movie) => (
            <Card key={movie.id} style={{ maxWidth: '18rem', margin: '10px' }}>
              <div className='card-img-wrapper'>
                <Card.Img className='card-img' variant="top" src={movie.imageUrl} alt={movie.title} />
              </div>
              <Card.Body>
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text>
                  {movie.description}
                </Card.Text>
                <Button variant="primary" onClick={() => onBookClick(movie)}>{buttonText}</Button>
              </Card.Body>
            </Card>
          ))
        ) : (
          <p>No movies available.</p>
        )}
      </div>
    </div>
  );
};

export default MoviesComponent;
