import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import axios from 'axios';

const BookingModal = ({ show, onHide, movieTitle }) => {
  const [numberOfSeats, setNumberOfSeats] = useState('');
  const [bookingDate, setBookingDate] = useState('');
  const [bookingTime, setBookingTime] = useState('');

  const showTimes = ['10:00 AM', '1:30 PM', '5:30 PM', '8:30 PM'];

  const handleSubmit = async (event) => {
    event.preventDefault();

    const user = JSON.parse(localStorage.getItem('user'));

    const bookingData = {
      userId: user.id,
      username: user.name,
      email: user.email,
      movieTitle,
      numberOfSeats,
      bookingDate,
      bookingTime
    };

    try {
      await axios.post('http://localhost:8080/api/bookings', bookingData);
      alert('Booking successful!');
      onHide(); // Close the modal
    } catch (error) {
      console.error('Error booking movie:', error);
      alert('Error booking movie. Please try again.');
    }
  };

  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Book Tickets for {movieTitle}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formNumberOfSeats">
            <Form.Label>Number of Seats</Form.Label>
            <Form.Control
              type="number"
              value={numberOfSeats}
              onChange={(e) => setNumberOfSeats(e.target.value)}
              placeholder="Enter number of seats"
            />
          </Form.Group>
          <Form.Group controlId="formBookingDate">
            <Form.Label>Booking Date</Form.Label>
            <Form.Control
              type="date"
              value={bookingDate}
              onChange={(e) => setBookingDate(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBookingTime">
            <Form.Label>Booking Time</Form.Label>
            <Form.Select
              value={bookingTime}
              onChange={(e) => setBookingTime(e.target.value)}
            >
              <option value="">Select showtime</option>
              {showTimes.map((time, index) => (
                <option key={index} value={time}>
                  {time}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
          <Button variant="secondary" onClick={onHide}>
            Close
          </Button>
          <Button variant="primary" type="submit">
            Book Now
          </Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default BookingModal;
