import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Spinner from 'react-bootstrap/Spinner';
import './Main.css';

const API_BASE_URL = 'http://localhost:8080/api/movies'; // Update if necessary

const MoviesComponent = ({ isLoggedIn, onBookClick }) => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get(API_BASE_URL);
        setMovies(response.data);
      } catch (error) {
        setError('Error fetching movies. Please try again later.');
        console.error('Error fetching movies:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchMovies();
  }, []);

  const handleDetailsClick = async (movieId) => {
    try {
      const response = await axios.get(`${API_BASE_URL}/${movieId}`);
      setSelectedMovie(response.data);
    } catch (error) {
      console.error('Error fetching movie details:', error);
    }
  };

  if (loading) return <Spinner animation="border" variant="primary" />;
  if (error) return <p>{error}</p>;

  return (
    <div className='main'>
      <div className='card-container'>
        {movies.length > 0 ? (
          movies.map((movie) => (
            <Card key={movie.id} style={{ maxWidth: '18rem', margin: '10px' }}>
              <div className='card-img-wrapper'>
                <Card.Img className='card-img' variant="top" src={movie.imageUrl} alt={movie.title} />
              </div>
              <Card.Body>
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text>
                  {movie.description}
                </Card.Text>
                <Button
                  variant="primary"
                  onClick={() => {
                    if (isLoggedIn) {
                      handleDetailsClick(movie.id);
                    } else {
                      alert('Please log in to view details.');
                    }
                  }}
                >
                  {isLoggedIn ? 'Details' : 'Login to Book'}
                </Button>
                {selectedMovie && selectedMovie.id === movie.id && (
                  <div className="movie-details">
                    <h3>More Details</h3>
                    <p><strong>Title:</strong> {selectedMovie.title}</p>
                    <p><strong>Description:</strong> {selectedMovie.description}</p>
                    <p><strong>Release Date:</strong> {selectedMovie.releaseDate}</p>
                    <p><strong>Genre:</strong> {selectedMovie.genre}</p>
                    <p><strong>Cast Time:</strong> {selectedMovie.castTime}</p>
                    {/* Add more details as needed */}
                  </div>
                )}
              </Card.Body>
            </Card>
          ))
        ) : (
          <p>No movies available.</p>
        )}
      </div>
    </div>
  );
};

export default MoviesComponent;
