import './UserPage.css';
import search_icon from '../assets/search_icon.png';
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import userImage from '../assets/User.png';
import MoviesComponent from '../Components/Main/Main'; // Ensure the path is correct
import BookingModal from '../Pages/BookingModal'; // Ensure the path is correct

const handleLogout = (navigate) => {
    // Remove JWT token from localStorage
    localStorage.removeItem('token');
   
    
    // Optionally, send a request to the backend logout endpoint
    fetch('/api/auth/logout', {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        },
    }).then(() => {
        // Navigate to the login page
        navigate('/');
    });
};

const UserPage = () => {
    const [user, setUser] = useState(null);
    const [isSearching, setIsSearching] = useState(false);
    const [searchText, setSearchText] = useState('');
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [showBookingModal, setShowBookingModal] = useState(false);
    const [selectedMovie, setSelectedMovie] = useState(null);
    const navigate = useNavigate();
  
    const handleBookClick = (movie) => {
      setSelectedMovie(movie);
      setShowBookingModal(true);
    };
  
    const buttonText = user ? 'Book' : 'Details';
  
    return (
      <div>
        <div className="navbar">
          {/* ... */}
        </div>
  
        <MoviesComponent buttonText={buttonText} onBookClick={handleBookClick} />
  
        {selectedMovie && (
          <BookingModal
            show={showBookingModal}
            onHide={() => setShowBookingModal(false)}
            movieTitle={selectedMovie.title}
          />
        )}
      </div>
    );
  };

export default UserPage;
