import React, { useState, useEffect } from 'react';
import { getUser, updateUser } from '../api';
import './Settings.css';

const Settings = () => {
    const [formData, setFormData] = useState({
      username: '',
      email: '',
      name: '',
      password: '',
      confirmPassword: ''
    });
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});
    const [user, setUser] = useState(null);
  
    useEffect(() => {
        const fetchUserData = async () => {
          try {
            const username = localStorage.getItem('username');
            
            if (username) {
              const { data } = await getUser(username); // Assuming getUser returns { data }
              setFormData({
                username: data.username,
                email: data.email,
                name: data.name,
                password: '',
                confirmPassword: ''
              });
              setUser(data);
            } else {
              setError('Username not found in localStorage');
            }
          } catch (err) {
            setError(err.message || 'Error fetching user data');
          } finally {
            setLoading(false);
          }
        };
      
        fetchUserData();
      }, []);
      
  
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
        setErrors(prevErrors => ({ ...prevErrors, [name]: '' }));
    };
  
    const validateForm = () => {
        const newErrors = {};
        if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Invalid email address';
        }
        if (formData.password && formData.password.length < 8) {
            newErrors.password = 'Password must be at least 8 characters long';
        }
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = 'Passwords do not match';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };
  
    const handleFormSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;
  
        setLoading(true);
        try {
            const dataToSend = { ...formData };
            if (dataToSend.password) {
                dataToSend.password = btoa(dataToSend.password); // Base64 encode password
            }
            delete dataToSend.confirmPassword;
  
            const updatedUser = await updateUser(dataToSend);
            setMessage('Profile updated successfully!');
            localStorage.setItem('username', updatedUser.username);
        } catch (error) {
            console.error('Error response:', error.response ? error.response.data : 'No response data');
            console.error('Error message:', error.message);
            setMessage(`Error updating profile: ${error.response ? error.response.data : error.message}`);
        } finally {
            setLoading(false);
        }
    };
  
    if (loading) {
        return <div>Loading...</div>;
    }
  
    return (
        <div className="settings-container">
            <h1>Account Settings</h1>
            {message && <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>{message}</p>}
            <form onSubmit={handleFormSubmit} className="settings-form">
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={formData.username}
                        onChange={handleInputChange}
                        disabled
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                    />
                    {errors.email && <span className="error">{errors.email}</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="password">New Password</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleInputChange}
                        placeholder="Leave blank to keep current password"
                    />
                    {errors.password && <span className="error">{errors.password}</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="confirmPassword">Confirm New Password</label>
                    <input
                        type="password"
                        id="confirmPassword"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleInputChange}
                        placeholder="Confirm new password"
                    />
                    {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}
                </div>
                <button type="submit" className="btn-save" disabled={loading}>
                    {loading ? 'Saving...' : 'Save Changes'}
                </button>
            </form>
        </div>
    );
};

export default Settings;
