import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import MoviesComponent from '../Components/Main/Main';
import BookingModal from '../Pages/BookingModal';
import './UserPage.css';
import userImage from '../assets/User.png';
import search_icon from '../assets/search_icon.png';

const handleLogout = (navigate) => {
  localStorage.removeItem('token');
  localStorage.removeItem('user'); // Clear user data from localStorage on logout
  fetch('/api/auth/logout', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
    },
  }).then(() => {
    navigate('/');
  });
};

const UserPage = () => {
  const [user, setUser] = useState(null);
  const [isSearching, setIsSearching] = useState(false);
  const [searchText, setSearchText] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [showBookingModal, setShowBookingModal] = useState(false);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [movies, setMovies] = useState([]); // Store all movies
  const [filteredMovies, setFilteredMovies] = useState([]); // Filtered movies for search results
  const navigate = useNavigate();

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      try {
        const parsedUser = JSON.parse(userData);
        setUser(parsedUser);
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    } else {
      navigate('/welcome', { replace: true });
    }

    // Fetch movies from the backend
    fetch('/api/movies')
      .then((res) => res.json())
      .then((data) => {
        setMovies(data);
        setFilteredMovies(data); // Initially, show all movies
      })
      .catch((error) => console.error('Error fetching movies:', error));
  }, [navigate]);

  const handleBlur = () => {
    if (!searchText) {
      setIsSearching(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchText(value);
    handleSearch(value);
  };

  const handleSearchIconClick = () => {
    setIsSearching(true);
  };

  const handleSearch = (query) => {
    if (query.trim() === '') {
      setFilteredMovies(movies); // If no query, show all movies
    } else {
      const filtered = movies.filter((movie) =>
        movie.title.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredMovies(filtered);
    }
  };

  const handleToggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleDropdownClick = (action) => {
    if (action === 'logout') {
      handleLogout(navigate);
    } else if (action === 'settings') {
      navigate('/settings');
    }
    setIsDropdownOpen(false);
  };

  const handleBookClick = (movie) => {
    setSelectedMovie({
      id: movie.id,
      title: movie.title,
      genre: movie.genre,
    });
    setShowBookingModal(true);
  };

  const handleCloseBookingModal = () => {
    setShowBookingModal(false);
    setSelectedMovie(null);
  };

  const isLoggedIn = !!user;

  return (
    <div>
      <div className="navbar">
        <h1 className="logo">Hey Movies</h1>

        <div className="navbar-right">
          <div className="userDetails">
            {user ? (
              <div>
                <p>Welcome, {user.name || 'User'}</p>
              </div>
            ) : (
              <p>No user data found</p>
            )}
          </div>
          <img
            src={userImage}
            alt="user"
            className="user-image"
            onClick={handleToggleDropdown}
          />

          <div className={`dropdown-menu ${isDropdownOpen ? 'show' : ''}`}>
            <li className="dropdown-item" onClick={() => handleDropdownClick('settings')}>Setting</li>
            <li className="dropdown-item" onClick={() => handleDropdownClick('logout')}>Log Out</li>
          </div>

          {isSearching ? (
            <input
              type="text"
              value={searchText}
              onChange={handleInputChange}
              onBlur={handleBlur}
              placeholder="Search..."
              aria-label="Search movies"
            />
          ) : (
            <img
              src={search_icon}
              alt="Search"
              onClick={handleSearchIconClick}
              className="search-icon"
            />
          )}
        </div>
      </div>

      <MoviesComponent
        movies={filteredMovies} // Pass filtered movies to the MoviesComponent
        buttonText={isLoggedIn ? 'Book' : 'Details'}
        isLoggedIn={isLoggedIn}
        onBookClick={handleBookClick}
      />

      {selectedMovie && (
        <BookingModal
          show={showBookingModal}
          onHide={handleCloseBookingModal}
          movieId={selectedMovie.id}
          movieTitle={selectedMovie.title}
          genre={selectedMovie.genre}
        />
      )}
    </div>
  );
};

export default UserPage;
