import React, { useState, useEffect } from 'react';
import { API_ENDPOINTS, updateUser, getUser } from '../api';
import './Settings.css';

const Settings = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        name: '',
        password: '',
        confirmPassword: ''
    });
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});

    useEffect(() => {
        fetchUserData();
    }, []);

    const fetchUserData = async () => {
        setLoading(true);
        try {
            const username = localStorage.getItem('username');
            const userData = await getUser(username);
            setFormData(prevState => ({
                ...prevState,
                username: userData.username || '',
                email: userData.email || '',
                name: userData.name || ''
            }));
        } catch (error) {
            setMessage('Failed to load user data. Please try again later.');
            console.error('Error fetching user data:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
        // Clear error when user starts typing
        setErrors(prevErrors => ({ ...prevErrors, [name]: '' }));
    };

    const validateForm = () => {
        const newErrors = {};
        if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Invalid email address';
        }
        if (formData.password && formData.password.length < 8) {
            newErrors.password = 'Password must be at least 8 characters long';
        }
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = 'Passwords do not match';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        setLoading(true);
        try {
            const dataToSend = { ...formData };
            if (dataToSend.password) {
                // In a real app, use a proper hashing algorithm here
                dataToSend.password = btoa(dataToSend.password);
            }
            delete dataToSend.confirmPassword;

            const updatedUser = await updateUser(dataToSend);
            setMessage('Profile updated successfully!');
            // Update localStorage if needed
            localStorage.setItem('username', updatedUser.username);
        } catch (error) {
            setMessage('Error updating profile. Please try again.');
            console.error('Error updating profile:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="settings-container">
            <h1>Account Settings</h1>
            {message && <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>{message}</p>}
            <form onSubmit={handleFormSubmit} className="settings-form">
                {/* Form fields... */}
                <button type="submit" className="btn-save" disabled={loading}>
                    {loading ? 'Saving...' : 'Save Changes'}
                </button>
            </form>
        </div>
    );
};

export default Settings;
