import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { API_ENDPOINTS, updateUser, getUser } from '../api';  // Update the path if necessary
import './Settings.css';

const Settings = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        name: '',
        password: '',
        confirmPassword: ''
    });
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});

    useEffect(() => {
        fetchUserData();
    }, []);

    const fetchUserData = async () => {
        setLoading(true);
        try {
            const username = localStorage.getItem('username');
            const response = await axios.get(API_ENDPOINTS.GET_USER, {
                params: { username }
            });

            if (response.data) {
                const userData = response.data;
                setFormData(prevState => ({
                    ...prevState,
                    username: userData.username || '',
                    email: userData.email || '',
                    name: userData.name || ''
                }));
            }
        } catch (error) {
            setMessage('Failed to load user data. Please try again later.');
            console.error('Error fetching user data:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
        // Clear error when user starts typing
        setErrors(prevErrors => ({ ...prevErrors, [name]: '' }));
    };

    const validateForm = () => {
        const newErrors = {};
        if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Invalid email address';
        }
        if (formData.password && formData.password.length < 8) {
            newErrors.password = 'Password must be at least 8 characters long';
        }
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = 'Passwords do not match';
        }
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        setLoading(true);
        try {
            const dataToSend = { ...formData };
            if (dataToSend.password) {
                // In a real app, use a proper hashing algorithm here
                dataToSend.password = btoa(dataToSend.password);
            }
            delete dataToSend.confirmPassword;

            const response = await axios.put(API_ENDPOINTS.UPDATE_USER, dataToSend, {
                headers: { 'Content-Type': 'application/json' }
            });

            if (response.data) {
                setMessage('Profile updated successfully!');
                // Update localStorage if needed
                localStorage.setItem('user', JSON.stringify(response.data));
            }
        } catch (error) {
            setMessage('Error updating profile. Please try again.');
            console.error('Error updating profile:', error);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="settings-container">
            <h1>Account Settings</h1>
            {message && <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>{message}</p>}
            <form onSubmit={handleFormSubmit} className="settings-form">
                <div className="form-group">
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        readOnly
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        required
                    />
                    {errors.email && <span className="error">{errors.email}</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="name">Full Name</label>
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">New Password</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleInputChange}
                        placeholder="Enter new password if you want to change it"
                    />
                    {errors.password && <span className="error">{errors.password}</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="confirmPassword">Confirm New Password</label>
                    <input
                        type="password"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleInputChange}
                        placeholder="Confirm new password"
                    />
                    {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}
                </div>
                <button type="submit" className="btn-save" disabled={loading}>
                    {loading ? 'Saving...' : 'Save Changes'}
                </button>
            </form>
        </div>
    );
};

export default Settings;
