import './UserPage.css';
import search_icon from '../assets/search_icon.png';
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import userImage from '../assets/User.png';
import MoviesComponent from '../Components/Main/Main';
import BookingModal from '../Components'; // Ensure the path is correct

const handleLogout = (navigate) => {
    // Remove JWT token from localStorage
    localStorage.removeItem('token');
    
    // Optionally, send a request to the backend logout endpoint
    fetch('/api/auth/logout', {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        },
    }).then(() => {
        // Navigate to the login page
        navigate('/');
    });
};

const UserPage = () => {
    const [user, setUser] = useState(null);
    const [isSearching, setIsSearching] = useState(false);
    const [searchText, setSearchText] = useState('');
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [showBookingModal, setShowBookingModal] = useState(false);
    const [selectedMovie, setSelectedMovie] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const userData = localStorage.getItem('user');

        if (userData) {
            try {
                const parsedUser = JSON.parse(userData);
                setUser(parsedUser);
            } catch (error) {
                console.error('Error parsing user data:', error);
            }
        } else {
            navigate('/welcome', { replace: true });
        }
    }, [navigate]);

    const handleBlur = () => {
        if (!searchText) {
            setIsSearching(false);
        }
    };

    const handleInputChange = (e) => {
        setSearchText(e.target.value);
    };

    const handleSearchIconClick = () => {
        setIsSearching(true);
    };

    const handleToggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    const handleDropdownClick = (action) => {
        if (action === 'logout') {
            handleLogout(navigate);
        } else if (action === 'settings') {
            navigate('/settings');
        }
        setIsDropdownOpen(false); 
    };

    // Define handleBookClick
    const handleBookClick = (movie) => {
        setSelectedMovie(movie);
        setShowBookingModal(true);
    };

    // Define handleCloseBookingModal
    const handleCloseBookingModal = () => {
        setShowBookingModal(false);
        setSelectedMovie(null);
    };

    return (
        <div>
            <div className="navbar">
                <h1 className="logo">Hey Movies</h1>

                <div className="navbar-right">
                    <div className="userDetails">
                        {user ? (
                            <div>
                                <p>Welcome, {user.name || 'User'}</p>
                            </div>
                        ) : (
                            <p>No user data found</p>
                        )}
                    </div>
                    <img
                        src={userImage}
                        alt="user"
                        className="user-image"
                        onClick={handleToggleDropdown}
                    />

                    <div className={`dropdown-menu ${isDropdownOpen ? 'show' : ''}`}>
                        <li className="dropdown-item" onClick={() => handleDropdownClick('settings')}>Setting</li>
                        <li className="dropdown-item" onClick={() => handleDropdownClick('logout')}>Log Out</li>
                    </div>

                    {isSearching ? (
                        <input
                            type="text"
                            value={searchText}
                            onChange={handleInputChange}
                            onBlur={handleBlur}
                            placeholder="Search..."
                            aria-label="Search movies"
                        />
                    ) : (
                        <img
                            src={search_icon}
                            alt="Search"
                            onClick={handleSearchIconClick}
                            className="search-icon"
                        />
                    )}
                </div>
            </div>

            <MoviesComponent buttonText="Book" onBookClick={handleBookClick} />

            {selectedMovie && (
                <BookingModal
                    show={showBookingModal}
                    onHide={handleCloseBookingModal}
                    movieTitle={selectedMovie.title}
                />
            )}
        </div>
    );
}

export default UserPage;
