import React from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import img1 from '../../assets/technological-exploration-settlement.jpg';
import './Main.css';
import axios from 'axios';
import  { useState, useEffect } from 'react';

const Main = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await axios.get('http://localhost:9000/api/movies');
        setMovies(response.data);
      } catch (error) {
        console.error('Error fetching movies:', error);
      }
    };
    fetchMovies();
  }, []);


  return (
    <div className='main'>
      <div className='card-container'>
        {movies.map((movie) => (
          <Card key={movie.id} style={{ width: '18rem', margin: '10px' }}>
            <Card.Img className='imgs' variant="top" src={movie.imageUrl} alt={movie.title} />
            <Card.Body>
              <Card.Title>{movie.title}</Card.Title>
              <Card.Text>
                {movie.description}
              </Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card>
        ))}
      </div>
    </div>
  );
};


export default Main;
