import axios from 'axios';

const API_BASE_URL = 'http://localhost:8080/api';

export const API_ENDPOINTS = {
  LOGIN: `${API_BASE_URL}/auth/login`,
  REGISTER: `${API_BASE_URL}/auth/register`,
  UPDATE_USER: `${API_BASE_URL}/auth/update`,
  GET_USER: `${API_BASE_URL}/auth/user`,
  LOGOUT: `${API_BASE_URL}/auth/logout`,
};

export const loginUser = async (formData) => {
  const response = await fetch('http://localhost:8080/api/auth/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(formData),
  });

  if (!response.ok) {
    const errorText = await response.text(); // Handle plain text error
    throw new Error(errorText || 'Network response was not ok.');
  }

  return response.json(); // Assuming the success response is JSON
};
export const registerUser = async (userData) => {
  try {
    const response = await axios.post(API_ENDPOINTS.REGISTER, userData);
    return response.data;
  } catch (error) {
  
    if (error.response) {
      throw new Error(error.response.data.message || 'Registration failed');
    }
    throw new Error('Network error');
  }
};

export const updateUser = async (userData) => {
  try {
      const response = await axios.put('http://localhost:8080/api/auth/update', userData);
      return response.data;
  } catch (error) {
      throw error;
  }
};

export const getUser = async (username) => {
  try {
    const response = await axios.get(API_ENDPOINTS.GET_USER, { params: { username } });
    return response.data;
  } catch (error) {
    throw error.response ? error.response.data : new Error('Network error');
  }
};
